package loops;

public class w2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * for/while/do while
		 * 
		 * initialisation:  where to start 
		 * 
		 * condition:  where to stop
		 *             frwed: <= endvalue
		 *             backward: >=end value
		 *             
		 * inc/dec   : diff between 2 numbers 
		 * initialisation
		 * do
		 * {
		 * statement
		 * inc/dec
		 * }
		 * while(condition);
		 * 
		 * 6th table
		 * */
		
		int i=0;
		do
		{
			System.out.println(i);
			i+=6;
		}
		while(i<=60);
	}

}
